import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';
import { ErrorService } from './error.service';

class Post {
  public username: string;
  public email: string;
  public password: string;
}
@Injectable({
  providedIn: 'root'
})
export class CrudService {

  constructor(private http: HttpClient, private errorservice: ErrorService
  ) { }

  createUser(user): Observable<Post[]> {
    return this.http.post<Post[]>('http://localhost:3000/posts', user);
  }
  // tslint:disable-next-line: typedef
  getAllUser() {
    return this.http.get('http://localhost:3000/posts')
      .pipe(
        retry(1),
        catchError(this.errorservice.handleError)
      );
  }
  // tslint:disable-next-line: typedef
  deleteUser(user) {
    return this.http.delete('http://localhost:3000/posts/' + user.id);
  }
  // tslint:disable-next-line: typedef
  updateUser(user) {
    return this.http.put('http://localhost:3000/posts/' + user.id, user);
  }
}

import { Injectable } from '@angular/core';

export const darkTheme = {
  'primary-color': '#455363',
  'background-color': '#1f2935',
  'text-color': '#fff'
  };
export const lightTheme = {
     'primary-color': '#fff',
'background-color': '#fff',
'text-color': '#2d2d2d'
};

@Injectable({
  providedIn: 'root'
})
export class DarkService {
  toggleDark() {
    this.setTheme(darkTheme);
     }
    toggleLight() {
    this.setTheme(lightTheme);
     }
    private setTheme(theme: {}) {
    Object.keys(theme).forEach(k =>
    document.documentElement.style.setProperty(`--${k}`, theme[k])
     );
     }
  constructor() { }
}

